package com.brayden;

import static org.junit.Assert.*;

import java.util.Arrays;

import org.junit.Test;

public class ShortestPalindrome {
    public String shortestPalindrome(String s) {
        if(s == null || s.isEmpty())
            return "";
        int i = s.length() - 1;
        for(; i >= 0; i--)
            if(isPal(s, 0, i))
                break;
        StringBuffer sb = new StringBuffer();
        sb.append(s.substring(i + 1)).reverse();
        return sb.toString() + s;
    }

    private boolean isPal(String s, int low, int high) {
        while(low < high)
            if(s.charAt(low++) != s.charAt(high--))
                return false;
        return true;
    }
    
    @Test
    public void test(){
        /**
Submission Result: Time Limit Exceeded
Last executed input:    "abbacd"
         */
        assertEquals("dcabbacd", shortestPalindrome("abbacd"));
    }
    
    @Test
    public void test1(){
        /**
Submission Result: Time Limit Exceeded
Last executed input:    ""
         */
        char[] arr = new char[655360];
        Arrays.fill(arr, 'a');
        arr[300000] = 'b';
        assertEquals("dcabbacd", shortestPalindrome(new String(arr)));
    }
}
