package com.brayden;

import static org.junit.Assert.*;

import java.util.Arrays;

import org.junit.Test;

public class ShortestPalindrome {
    
    public String shortestPalindrome3(String s) {
        if(s == null || s.isEmpty())
            return s;
        String revS = new StringBuilder(s).reverse().toString();

        int len = s.length();
        int[] p = new int[len];
        for (int i = 0; i < len; i++) {
            int j = p[i];
            while (j > 0 && revS.charAt(i) != s.charAt(j))
                j = p[j];
            p[i] = (j += (revS.charAt(i) == s.charAt(j)) ? 1 : 0);
        }
        return revS.substring(0, len - p[len - 1]) + s;
    }
    
    public String shortestPalindrome(String s) {
        if(s == null || s.isEmpty())
            return s;
        String revS = new StringBuilder(s).reverse().toString();

        int len = s.length();
        int[] p = new int[len];
        p[0] = (revS.charAt(0) == s.charAt(0)) ? 1 : 0;
        for (int i = 1; i < len; i++) {
            int j = p[i - 1];
            p[i] = revS.charAt(i) == s.charAt(j) ? j + 1 : 0;
        }
        return revS.substring(0, len - p[len - 1]) + s;
    }

    
    public String shortestPalindrome2(String s) {
        if(s == null || s.isEmpty())
            return "";
        String revS = new StringBuilder(s).reverse().toString();
        String l = s + "#" + revS;

        int[] p = new int[l.length()];
        for (int i = 1; i < l.length(); i++) {
            int j = p[i - 1];
            while (j > 0 && l.charAt(i) != l.charAt(j))
                j = p[j - 1];
            p[i] = (j += (l.charAt(i) == l.charAt(j)) ? 1 : 0);
        }

        return revS.substring(0, s.length() - p[l.length() - 1]) + s;
    }
    
    public String shortestPalindrome1(String s) {
        if(s == null || s.isEmpty())
            return "";
        int i = s.length() - 1;
        for(; i >= 0; i--)
            if(isPal(s, 0, i))
                break;
        StringBuffer sb = new StringBuffer();
        sb.append(s.substring(i + 1)).reverse();
        return sb.toString() + s;
    }

    private boolean isPal(String s) {
        return isPal(s, 0, s.length() - 1);
    }
    
    private boolean isPal(String s, int low, int high) {
        while(low < high)
            if(s.charAt(low++) != s.charAt(high--))
                return false;
        return true;
    }
    
    @Test
    public void test4(){
        /*
Submission Result: Wrong Answer
Input:  "aacecaaa"
Output:     "aaacecaacecaaa"
Expected:   "aaacecaaa"
         */
        assertEquals("aaacecaaa", shortestPalindrome("aacecaaa"));
    }
    
    @Test
    public void test3(){
        assertEquals("abcdadcba", shortestPalindrome("adcba"));
    }
    
    @Test
    public void test2(){
        assertEquals("aba", shortestPalindrome("aba"));
    }
    
    @Test
    public void test(){
        /**
Submission Result: Time Limit Exceeded
Last executed input:    "abbacd"
         */
        assertEquals("dcabbacd", shortestPalindrome("abbacd"));
    }
    
    @Test
    public void test1(){
        /**
Submission Result: Time Limit Exceeded
Last executed input:    ""
         */
        char[] arr = new char[655360];
        Arrays.fill(arr, 'a');
        arr[300000] = 'b';
        assertEquals("dcabbacd", shortestPalindrome(new String(arr)));
    }
}
